mapper.select:
select count(*) 
  from product 
 where m_no = #{mNo} and deal_type = #{dealType}

select pro.p_no, pro.m_no, pro.p_c_no, pro.price, pro.quality, pro.p_lat, pro.p_lon, cate.p_high_cate, cate.p_low_cate, cate.p_keyword, pho.p_ph_path, pho.p_ph_name, mem.id, mem.nname, mem.m_photo, mem.like_cnt, (6371 * acos(cos(radians(#{lat})) * cos(radians(pro.p_lat)) * cos(radians(#{lon}) - radians(pro.p_lon)) + sin(radians(#{lat})) * sin(radians(pro.p_lat)))) as distance from product pro, pro_categorie cate, pro_photo pho, member mem where cate.p_c_no = pro.p_c_no and pro.p_no = pho.p_no and mem.m_no = pro.m_no and cate.p_high_cate = #{pHighCate}
and cate.p_low_cate = #{pLowCate}
and pho.p_ph_thum = 'y' and pro.deal_type = 's' and pro.deal_step = 'y' order by distance asc;

mapper.delete:
delete 
  from pro_comment
 where m_no = #{mNo}
   and p_c_no = #{pcNo}

mapper.select:
select count(*)
  from pro_comment
 where p_no = #{pNo}

select pro.p_no, pro.m_no, pro.p_c_no, pro.price, pro.p_content, date_format(pro.p_reg_date, "%Y년 %c월 %e일 %H:%i") p_reg_date, pro.deal_type, pro.quality, pro.deal_opt, pro.p_addr, pro.p_lat, pro.p_lon, pho.p_ph_no, pho.p_ph_path, pho.p_ph_name, cate.p_keyword, cate.p_high_cate, cate.p_low_cate, (select count(*) from pro_comment com where com.p_no=pro.p_no) comment_cnt from product pro, pro_photo pho, pro_categorie cate where pro.p_no = pho.p_no and pro.p_c_no = cate.p_c_no and pho.p_ph_thum='y' and pro.deal_step = 'y'
and cate.p_high_cate = #{pHighCate}
and cate.p_low_cate = #{pLowCate}
and cate.p_keyword like '%'||#{pKeyword}||'%'
order by pro.p_no desc

select * 
  from pro_categorie
 where p_high_cate = #{pHighCate}

   and p_low_cate = #{pLowCate}

 order by p_keyword asc

select * 
  from pro_photo 
 where p_ph_thum='y' and p_no in (
									select p_no 
									  from product 
									 where deal_type='s' and deal_step='y' and p_c_no in (
																		select p_c_no 
																		  from pro_categorie 
																		 where p_high_cate = #{pHighCate}
																		)
								 ) order by rand() limit 1;
