mapper.select:
select * 
  from pro_photo 
 where p_ph_thum='y' and p_no in (
									select p_no 
									  from product 
									 where deal_type='s' and p_c_no in (
																		select p_cate_no 
																		  from pro_categorie 
																		 where p_high_cate = #{pHighCate}
																		)
								 ) order by rand() limit 1;
