mapper.select:
select pro.*, pho.p_ph_no, pho.p_ph_path, pho.p_ph_name, cate.p_keyword, cate.p_high_cate, cate.p_low_cate from product pro, pro_photo pho, pro_categorie cate where pro.p_no = pho.p_no and pro.p_c_no = cate.p_c_no and pho.p_ph_thum='y'
and cate.p_high_cate = #{pHighCate}
and cate.p_low_cate = #{pLowCate}
and cate.p_keyword like '%'||#{pKeyword}||'%'
order by pro.p_no desc

select * 
  from pro_categorie
 where p_high_cate = #{pHighCate}

   and p_low_cate = #{pLowCate}

 order by p_keyword asc

select * 
  from pro_photo 
 where p_ph_thum='y' and p_no in (
									select p_no 
									  from product 
									 where deal_type='s' and deal_step='y' and p_c_no in (
																		select p_c_no 
																		  from pro_categorie 
																		 where p_high_cate = #{pHighCate}
																		)
								 ) order by rand() limit 1;
